import React, { useState } from "react";
import styles from "../styles/Itineraries.module.css";

const Itineraries = () => {
  const [tripDetails, setTripDetails] = useState({
    destination: "",
    startDate: "",
    endDate: "",
    travelers: 1,
    budget: "",
  });

  const [travelPlan, setTravelPlan] = useState([]); // Chat-GPTからのプランデータを格納
  const [chatMessages, setChatMessages] = useState([]);
  const [userMessage, setUserMessage] = useState("");

  // Chat-GPTからの応答を模擬
  const simulateGPTResponse = () => {
    const mockPlan = {
      days: [
        {
          day: "Day 1",
          activities: [
            { time: "10:00", description: "東京スカイツリー観光", cost: 1500 },
            { time: "14:00", description: "浅草寺散策", cost: 0 },
          ],
        },
        {
          day: "Day 2",
          activities: [
            { time: "09:00", description: "上野動物園", cost: 600 },
            { time: "12:00", description: "アメ横ショッピング", cost: 0 },
          ],
        },
      ],
    };

    setChatMessages([
      ...chatMessages,
      { sender: "bot", text: "旅行プランを生成しました。" },
    ]);

    setTravelPlan(mockPlan.days); // 状態にプランデータを格納
  };

  const handleChatSend = () => {
    if (userMessage.trim() === "") return;

    // ユーザーのメッセージを追加
    setChatMessages([...chatMessages, { sender: "user", text: userMessage }]);

    // Chat-GPTからの応答を模擬
    setTimeout(() => simulateGPTResponse(), 1000);

    // メッセージ入力をリセット
    setUserMessage("");
  };

  return (
    <div className={styles.travelPlanner}>
      <div className={styles.mainSection}>
        <h1>旅行プランニングサービス</h1>

        <div className={styles.activitySection}>
          <h2>旅行プラン</h2>
          {travelPlan.length === 0 ? (
            <p>旅行プランはまだありません。</p>
          ) : (
            travelPlan.map((day, index) => (
              <div key={index} className={styles.dayPlan}>
                <h3>{day.day}</h3>
                {day.activities.map((activity, idx) => (
                  <div key={idx} className={styles.activityItem}>
                    <span>{activity.time}</span>
                    <span>{activity.description}</span>
                    <span>¥{activity.cost}</span>
                  </div>
                ))}
              </div>
            ))
          )}
        </div>
      </div>

      <div className={styles.chatSection}>
        <h2>Chat-GPTとやりとり</h2>
        <div className={styles.chatBox}>
          {chatMessages.map((msg, index) => (
            <div
              key={index}
              className={`${styles.chatMessage} ${
                msg.sender === "user"
                  ? styles.userMessage
                  : styles.botMessage
              }`}
            >
              {msg.text}
            </div>
          ))}
        </div>
        <div className={styles.chatInput}>
          <input
            type="text"
            placeholder="質問や要望を入力"
            value={userMessage}
            onChange={(e) => setUserMessage(e.target.value)}
          />
          <button onClick={handleChatSend}>送信</button>
        </div>
      </div>
    </div>
  );
};

export default Itineraries;
